import React from 'react';
import css from './MyPosts.module.css'
import Post from './Post/Post';
import NewPost from './NewPost/NewPost';
import { addPostActionCreator, updateNewPostActionCreator } from '../../../redux/profileReduser'


const MyPosts = (props) => {

  let addPost = () => {
    props.dispatch(addPostActionCreator())
  }

  let onPostChange = (text) => {
    props.dispatch(updateNewPostActionCreator(text))
  }

    return (
        <div className={css.posts}>
          <h2>My Posts</h2>
          <NewPost updateNewPostText={onPostChange } 
                   newPostText={props.posts.newPostText} 
                   addPost={addPost}/>
          <ul className={css.posts__list}>

            {props.posts.posts.map(item => {
              return (<Post message={item.message} 
                           name={item.name}
                           imgUrl={item.url}
                           key={item.id}
                    />)
            })}
          </ul>
        </div>
    )
}

export default MyPosts;
