import React from 'react';
import Users from './Users';
import { follow, unfollow, setUsers, pickPage, setTotalCount, fetch, toggleFollowingProgress,getUsersThunkCreator } from "../../redux/usersReduser";
import { connect } from "react-redux";
import { getUsers } from '../../api/api';


class UsersContainer extends React.Component {

    componentDidMount() {
        this.props.getUsers(this.props.currentPage, this.props.pageSize)
    }

    onPageChanged = (p) => {
        this.props.pickPage(p);
        this.props.getUsers(p, this.props.pageSize)
/*             .then(data => {

                this.props.setUsers(data.items)
                this.props.fetch() 
            } */

    }

    render() {

        return <Users pageSize={this.props.pageSize}
            totalCount={this.props.totalCount}
            currentPage={this.props.currentPage}
            onPageChanged={this.onPageChanged}
            follow={this.props.follow}
            unfollow={this.props.unfollow}
            users={this.props.users}
            isFetching={this.props.isFetching}
            toggleFollowingProgress = {this.props.toggleFollowingProgress}
            followingInProgress = {this.props.followingInProgress}
        />
    }
}

let mapStateToProps = (state) => {


    return {
        users: state.users.users,
        pageSize: state.users.pageSize,
        totalCount: state.users.totalCount,
        currentPage: state.users.currentPage,
        isFetching: state.users.isFetching,
        followingInProgress: state.users.followingInProgress,
    }
}

export default connect(mapStateToProps, 
    {follow, unfollow, setUsers, 
    pickPage, setTotalCount, fetch, 
    toggleFollowingProgress, getUsers: getUsersThunkCreator})(UsersContainer);
