import React from 'react';
import * as axios from 'axios';
import Users from './Users';
import { followAC, unfollowAC, setUsersAC, pickPageAC, setTotalCountAC, fetchAC } from "../../redux/usersReduser";
import { connect } from "react-redux";


class UsersContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
        };
    }

    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
            .then(res => {
                this.setState({ isFetching: !this.isFetching })
                this.props.setUsers(res.data.items)
                this.props.setTotalCount(res.data.totalCount)
            })
    }

    onPageChanged = (p) => {
        this.props.pickPage(p)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${p}&count=${this.props.pageSize}`)
            .then(res => {
                this.setState({ isFetching: !this.isFetching })

                this.props.setUsers(res.data.items)
            }
        )
    }

    render() {

        return <Users pageSize={this.props.pageSize}
            totalCount={this.props.totalCount}
            currentPage={this.props.currentPage}
            onPageChanged={this.onPageChanged}

            users={this.props.users}
            isFetching={this.props.isFetching}
        />
    }
}

let mapStateToProps = (state) => {


    return {
        users: state.users.users,
        pageSize: state.users.pageSize,
        totalCount: state.users.totalCount,
        currentPage: state.users.currentPage,
        isFetching: state.users.isFetching,
    }
}

let mapDispatchToProps = (dispatch) => {
    return {
        follow: (id) => {
            dispatch(followAC(id))
        },
        unfollow: (id) => {
            dispatch(unfollowAC(id))
        },
        setUsers: (users) => {
            dispatch(setUsersAC(users))
        },
        pickPage: (page) => {
            dispatch(pickPageAC(page))
        },
        setTotalCount: (totalCount) => {
            dispatch(setTotalCountAC(totalCount))
        },
        fetchHandler: () => {
            dispatch(fetchAC())
        }

    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UsersContainer);

