import React from 'react';
import css from './Users.module.css';
import { makeStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import * as axios from 'axios';
import avatarDefault from '../../assets/img/user.png'
import { CircularProgress } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1),
        },
    },
    small: {
        width: theme.spacing(3),
        height: theme.spacing(3),
    },
    large: {
        width: theme.spacing(10),
        height: theme.spacing(10),
    },
}));

class Users extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            isFetching: false,
        };
    }

    classes = () => useStyles();

    getUsers = () => {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
            .then(res => {
                this.setState({ isFetching: !this.isFetching })
                this.props.setUsers(res.data.items)
                this.props.setTotalCount(res.data.totalCount)
            })
    }

    componentDidMount() {
         this.getUsers() 
    }

    onPageChanged = (p) => {
        this.props.pickPage(p)
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${p}&count=${this.props.pageSize}`)
        .then(res => {
            this.setState({ isFetching: !this.isFetching })

            this.props.setUsers(res.data.items)
        })
    }

    render() {
        let pagesCount = Math.ceil(this.props.totalCount / this.props.pageSize);
        let pages = [];
        for (let i = 1; i <= pagesCount; i++) {
            pages.push(i)
        }
        return (
            <div className={css.container}>
                <div>
                    {

                        pages.map( (p,index) => {
                            return <button key={index}
                                    onClick={ () => {this.onPageChanged(p)}}
                                className={this.props.currentPage === p && css.current}>{p}</button>
                        })
                    }
                </div>
                {
                this.state.isFetching
                ?
                <ul>
                    {

                     this.props.users.map(u => {
                            return (
                                <li key={u.id} className={css.item}>
                                    <div className={css.wrapper}>
                                        <Avatar className={this.classes.large + ' ' + css.avatar}
                                            src={u.photos.small ? u.photos.small : avatarDefault}
                                            alt='avatar' />
                                        {
                                            u.followed
                                                ? <button onClick={() => { this.props.unfollow(u.id) }}>Unfollow</button>
                                                : <button onClick={() => { this.props.follow(u.id) }}>Follow</button>


                                        }
                                    </div>
                                    <div className={css.body}>
                                        <div className={css.top}>
                                            <div className={css.name}>{u.name}</div>
                                        </div>

                                        <div className={css.status}>{u.status}</div>
                                    </div>
                                </li>
                            )
                        })
                    }
                </ul>
                : <div className={css.progress}><CircularProgress color="secondary" /></div>

                }
            </div>
        )
    }
}

export default Users;
