import React from 'react';
import css from './Users.module.css';
import { makeStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1),
        },
    },
    small: {
        width: theme.spacing(3),
        height: theme.spacing(3),
    },
    large: {
        width: theme.spacing(10),
        height: theme.spacing(10),
    },
}));

const Users = (props) => {
debugger
    const classes = useStyles();
    const avatarDefault = 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSHiPzh4W55zPbpTUBJBJSYV0MwPEyOjzRaCoYtEuVVB_hutk5h&usqp=CAU'

    if (props.users.length === 0) {
        props.setUsers(
            [
                {
                   id: 1,
                   avatarUrl: 'https://sun9-31.userapi.com/c849020/v849020113/195ec6/dm8KtRenxiQ.jpg',
                   followed: true,
                   fullName: 'Nikita',
                   status: 'Каеф',
                   location: { city: 'SPB', country: 'Russia' }
               },
               {
                   id: 2,
                   avatarUrl: 'https://i.pinimg.com/736x/ed/16/b9/ed16b9f0a63be6e6ab3a35da48031fb9.jpg',
                   followed: false,
                   fullName: 'Yura',
                   status: 'Осенняя мразь',
                   location: { city: 'NY', country: 'USA' }
               } 
           ]
        )
    }
    props.setUsers(
        [
            {
               id: 1,
               avatarUrl: 'https://sun9-31.userapi.com/c849020/v849020113/195ec6/dm8KtRenxiQ.jpg',
               followed: true,
               fullName: 'Nikita',
               status: 'Каеф',
               location: { city: 'SPB', country: 'Russia' }
           },
           {
               id: 2,
               avatarUrl: 'https://i.pinimg.com/736x/ed/16/b9/ed16b9f0a63be6e6ab3a35da48031fb9.jpg',
               followed: false,
               fullName: 'Yura',
               status: 'Осенняя мразь',
               location: { city: 'NY', country: 'USA' }
           } 
       ]
    )

    return (
        <div className={css.container}>
            <ul>
                {
                    props.users.map(u => {
                        return (
                            <li key={u.id} className={css.item}>
                                <div className={css.wrapper}>
                                    <Avatar className={classes.large + ' ' + css.avatar}
                                        src={u.avatarUrl ? u.avatarUrl : avatarDefault}
                                        alt='avatar' />
                                    {
                                        u.followed 
                                        ? <button onClick={() => {props.unfollow(u.id)}}>Unfollow</button> 
                                        : <button onClick={() => {props.follow(u.id)}}>Follow</button> 


                                    }
                                </div>
                                <div className={css.body}>
                                    <div className={css.top}>
                                        <div className={css.name}>{u.fullName}</div>
                                        <div className={css.location}>{u.location.city + ' ' + u.location.country}</div>
                                    </div>

                                    <div className={css.status}>{u.status}</div>
                                </div>
                            </li>
                        )
                    })
                }
            </ul>
        </div>
    )
}

export default Users;
