import React from 'react';
import css from './MyPosts.module.css'
import NewPost from './NewPost/NewPost';
import { addPostActionCreator, updateNewPostActionCreator } from '../../../redux/profileReduser'
import PostsList from './PostsList/PostsList';
import { connect } from 'react-redux';


const MyPosts= (props) => {

  let state = props.store.getState();
  let posts = state.profile.posts.sort((a,b) => b.id - a.id)

  let addPost = () => {
    props.store.dispatch(addPostActionCreator())
  }

  let onPostChange = (text) => {
    props.store.dispatch(updateNewPostActionCreator(text))
  }

    return (
        <div className={css.posts}>
          <h2>My Posts</h2>
          <NewPost updateNewPostText={onPostChange } 
                   newPostText={state.profile.newPostText} 
                   addPost={addPost}/>
          <PostsList  posts={posts}/>
        </div>
    )
}

let mapStateToProps = (state) => {
  return {
    posts: state.profile
  }
}

let mapDispatchToProps = () => {
  return {

  }
}

const SuperMyPosts = connect(mapStateToProps,mapDispatchToProps)(MyPosts);

export default MyPosts;
