import { authAPI } from "../api/api";


const SET_USER_DATA = 'SET_USER_DATA';

export const authSuccess = (id, email, login) => ({ type: SET_USER_DATA, payload: {id, email, login} })

let initialState = {
    id: null,
    email: null,
    login: null,
    isAuth: false
}

export const authReduser = (state = initialState, action) => {

    switch (action.type) {

        case SET_USER_DATA:

            return {
                ...state,
                ...action.payload,
                isAuth: true
            }

        default:
            return state
    }
}

export const authMe = () => {
    return dispatch => {
        authMe().then(res => {
            debugger
                if (res.data.resultCode === 0) {
                    let { login, id, email } = res.data.data;
                    dispatch(authSuccess(id, email, login));
                    dispatch(setUser(id));
                }
            })
    }
}

export default authReduser;
