const SET_USER_DATA = 'SET_USER_DATA';

export const auth = (id, email, login) => ({ type: SET_USER_DATA, payload: {id, email, login} })

let initialState = {
    id: null,
    email: null,
    login: null,
    isAuth: false
}

export const authReduser = (state = initialState, action) => {
    debugger

    switch (action.type) {

        case SET_USER_DATA:

            return {
                ...state,
                ...action.payload,
                isAuth: true
            }

        default:
            return state
    }
}

export default authReduser;
