import React from 'react';
import css from './ProfileStatus.module.css'

class ProfileStatus extends React.Component {

  state = {
    editMode: false,
  }

  render() {
    return (
      <div>
        {
          this.state.editMode
            ? <input value={this.props.status} />
            : <span>{this.props.status}</span>
        }

      </div>
    )
  }
}

export default ProfileStatus;
