const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';


export const followAC = (id) => ({ type: FOLLOW, payload: id, })
export const unfollowAC = (id) => ({ type: UNFOLLOW, payload: id, })
export const setUsersAC = (users) => ({ type: SET_USERS, payload: users, })


let initialState = {
    users: [],
    totalCount: 0,
    pageSize: 3,


}

export const usersReduser = (state = initialState, action) => {

    switch (action.type) {
        case FOLLOW:
            let stateCopy = {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.payload) {
                        return { ...u, followed: true }
                    }
                    return u
                })
            }
            return stateCopy

        case UNFOLLOW: {
            let stateCopy = {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.payload) {
                        return { ...u, followed: false }
                    }
                    return u
                })
            }
            return stateCopy
        }
        case SET_USERS: {
            return {...state, users: [...state.users, ...action.payload]}
        }


        default:
            return state
    }
}

export default usersReduser;
