import React from 'react';
import css from './Users.module.css';
import { makeStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import avatarDefault from '../../assets/img/user.png'
import { CircularProgress } from '@material-ui/core';


const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1),
        },
    },
    small: {
        width: theme.spacing(3),
        height: theme.spacing(3),
    },
    large: {
        width: theme.spacing(10),
        height: theme.spacing(10),
    },
}));
const Classes = () => useStyles();

const Users = (props) => {

    debugger
    let pagesCount = Math.ceil(props.totalCount / props.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }

    return (
        <div className={css.container}>
            <div className={css.pageBtn-wrapper}>
                {

                    pages.map((p, index) => {
                        return (
                            <div className={css.pageBtn}>
                                <span key={index}
                                    onClick={() => { props.onPageChanged(p) }}
                                    className={props.currentPage === p && css.current}>{p}</span>
                            </div>
                        )
                    })
                }
            </div>
            {
                props.isFetching
                    ?
                    <ul>
                        {

                            props.users.map(u => {
                                return (
                                    <li key={u.id} className={css.item}>
                                        <div className={css.wrapper}>
                                            <Avatar className={Classes.large + ' ' + css.avatar}
                                                src={u.photos.small ? u.photos.small : avatarDefault}
                                                alt='avatar' />
                                            {
                                                u.followed
                                                    ? <button onClick={() => { props.unfollow(u.id) }}>Unfollow</button>
                                                    : <button onClick={() => { props.follow(u.id) }}>Follow</button>


                                            }
                                        </div>
                                        <div className={css.body}>
                                            <div className={css.top}>
                                                <div className={css.name}>{u.name}</div>
                                            </div>

                                            <div className={css.status}>{u.status}</div>
                                        </div>
                                    </li>
                                )
                            })
                        }
                    </ul>
                    : <div className={css.progress}><CircularProgress color="secondary" /></div>

            }
        </div>
    )
}



export default Users;
