import React from 'react';
import css from './NewPost.module.css'

const NewPost = (props) => {

let newPostEl = React.createRef();

let addPost = () => {
  props.dispatch({
    action: 'ADD_POST',
  })
}

let onPostChange = () => {
  let text = newPostEl.current.value;  
  props.dispatch({
    action: 'UPDATE_NEW_POST_TEXT',
    payload: text
  })
}

    return (
      <div className={css.posts__new}>
      <div>
        <textarea type="text" ref={newPostEl}  value={props.newPostText} onChange={onPostChange} />
        <button onClick={addPost} type="submit">Отправить</button>
      </div>
    </div>
    )
}

export default NewPost;
