import { setUser } from '../../redux/profileReduser';

class HeaderContainer extends React.Component {

    componentDidMount() {
        this.props.authMe()
/*         authMe().then(res => {
            debugger
                if (res.data.resultCode === 0) {
                    let { login, id, email } = res.data.data;
                    this.props.auth(id, email, login);
                     this.props.setUser(id) 
                }
            }) */
    }

    render() {
        return (
            <Header {...this.props} />
        )
    }

}

let mapStateToProps = (state) => {
    return {
        isAuth: state.auth.isAuth,
        login: state.auth.login,
        email: state.auth.email,
        id: state.auth.id,

    }
}


let withUrlDataContainerComponent = withRouter(HeaderContainer)

export default connect(mapStateToProps, { authMe })(withUrlDataContainerComponent);
