import React from 'react';
import css from './Users.module.css';
import { makeStyles } from '@material-ui/core/styles';
import Avatar from '@material-ui/core/Avatar';
import * as axios from 'axios';

const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& > *': {
            margin: theme.spacing(1),
        },
    },
    small: {
        width: theme.spacing(3),
        height: theme.spacing(3),
    },
    large: {
        width: theme.spacing(10),
        height: theme.spacing(10),
    },
}));

const Users = (props) => {
    const classes = useStyles();
    const avatarDefault = 'https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSHiPzh4W55zPbpTUBJBJSYV0MwPEyOjzRaCoYtEuVVB_hutk5h&usqp=CAU'

    if (props.users.length === 0) {

        axios.get('https://social-network.samuraijs.com/api/1.0/users')
        .then(res => {
            debugger
            props.setUsers()
        })

    }

    return (
        <div className={css.container}>
            <ul>
                {
                    props.users.map(u => {
                        return (
                            <li key={u.id} className={css.item}>
                                <div className={css.wrapper}>
                                    <Avatar className={classes.large + ' ' + css.avatar}
                                        src={u.avatarUrl ? u.avatarUrl : avatarDefault}
                                        alt='avatar' />
                                    {
                                        u.followed
                                            ? <button onClick={() => { props.unfollow(u.id) }}>Unfollow</button>
                                            : <button onClick={() => { props.follow(u.id) }}>Follow</button>


                                    }
                                </div>
                                <div className={css.body}>
                                    <div className={css.top}>
                                        <div className={css.name}>{u.fullName}</div>
                                        <div className={css.location}>{u.location.city + ' ' + u.location.country}</div>
                                    </div>

                                    <div className={css.status}>{u.status}</div>
                                </div>
                            </li>
                        )
                    })
                }
            </ul>
        </div>
    )
}

export default Users;
