const SEND_MESSAGE = 'SEND_MESSAGE';
const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE_NEW_MESSAGE_TEXT';

export const sendMessageActionCreator = () => ({ type: SEND_MESSAGE })
export const updateNewMessageActionCreator = (text) => {
    return {
        type: UPDATE_NEW_MESSAGE_TEXT,
        payload: text,
    }
}

let initialState = {
    users: [
        {
            id: 1,
            name: 'Никита',
            message: 'Во сколько встретимся?',
        },
        {
            id: 2,
            name: 'Юра',
            message: 'Захвати плед',
        },
        {
            id: 3,
            name: 'Марго',
            message: 'Короче. Щас скину. Вопрос в том как сделать чтобы картинка машины не прибивалась к правому краю',
        },
        {
            id: 4,
            name: 'Поля',
            message: 'Мартин покакал',
        },
        {
            id: 5,
            name: 'Morgenshtern',
            message: 'Ты думал бует басс?? НАЕБАЛ!!',
        },
        {
            id: 6,
            name: 'Я',
            message: 'Ты теперь мои заметки!',
        },
    ],
    message: [
        {
            id: 1,
            date: '12.04.20 12:47',
            text: 'Мартин задолбал есть и свинячить!',
        },
        {
            id: 2,
            date: '12.04.20 10:42',
            text: 'Погнали в маунтэнблейд!!!!',
        },
        {
            id: 3,
            date: '12.04.20 11:02',
            text: 'Я НЕ КРЫЫЫСАА!!!Короче она такая. Мощный ход Никита спрашивает: почему Она такая: ну это весьма непопулярное мнение',
        },
        {
            id: 4,
            date: '12.04.20 12:21',
            text: 'Я летаю словно рыс!',
        },
        {
            id: 5,
            date: '12.04.20 12:21',
            text: 'Я',
        },

    ],
    newMessageText: ''
}

const dialogsReduser = (state = initialState, action) => {

    let stateCopy = {
        ...state,
        message: [...state.message]
    }

    switch (action.type) {

        case SEND_MESSAGE: 

            if (stateCopy.newMessageText !== '') {
                debugger
                let date = new Date();
                let newMessage = {
                    id: stateCopy.message.length + 1,
                    date: date.getHours() + ':' + date.getMinutes(),
                    text: stateCopy.newMessageText,
                }


                stateCopy.message.push(newMessage)

                stateCopy.newMessageText = '';

                return stateCopy
            }


        case UPDATE_NEW_MESSAGE_TEXT: {
            let stateCopy = {...state}
            stateCopy.newMessageText = action.payload

            return stateCopy

        }

        default:
            return state
    }

}

export default dialogsReduser;
