import React from 'react';
import css from './NewMessage.module.css';
import { Field, reduxForm } from 'redux-form';


const NewMessage = (props) => {



    let onChangeMessage = (text) => {
        props.updateNewMessageText(text)
    }

    let onSendMessageClick = () => {
        props.sendMessage()
    }

    return (
        <div className={css.wrapper}>
            <NewMessageReduxForm />
        </div>
    )
}

const NewMessageForm = (props) => {
    return (
        <form className={css.newMessageForm} onSubmit={props.handleSubmit}>
            <Field onChange={onChangeMessage}
                name={"newMessage"}
                component={"textarea"}
                value={props.newMessageText}
                placeholder='Введите сообщение...'
            />
            <button type="submit" className={css.sub} onClick={onSendMessageClick}>send message</button>
        </form>
    )
}

const NewMessageReduxForm = reduxForm({ form: 'newMessageForm' })(NewMessageForm)


export default NewMessage;
