import React from "react";
import css from './FormControls.module.css'

export const Textarea = ({input, meta, ...props}) => {
    return (
        <div className={css.formControl + ' ' + css}>
            <textarea  {...input} {...props}></textarea>
            {
                meta.error
                ? <span>some error</span> 
                : null
            }

        </div>
    )
}
