const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';
const PICK_PAGE = 'PICK_PAGE';
const SET_TOTAL_COUNT = 'SET_TOTAL_COUNT';


export const followAC = (id) => ({ type: FOLLOW, payload: id, })
export const unfollowAC = (id) => ({ type: UNFOLLOW, payload: id, })
export const setUsersAC = (users) => ({ type: SET_USERS, payload: users, })
export const pickPageAC = (page) => ({ type: PICK_PAGE, payload: page, })
export const setTotalCount = (totalCount) => ({ type: SET_TOTAL_COUNT, payload: totalCount, })




let initialState = {
    users: [],
    totalCount: 0,
    pageSize: 3,
    currentPage: 1,
}

export const usersReduser = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW:
            let stateCopy = {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.payload) {
                        return { ...u, followed: true }
                    }
                    return u
                })
            }
            return stateCopy

        case UNFOLLOW: {
            let stateCopy = {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.payload) {
                        return { ...u, followed: false }
                    }
                    return u
                })
            }
            return stateCopy
        }
        case SET_USERS: {
            return { ...state, users: action.payload }
        }

        case PICK_PAGE:
            return { ...state, currentPage: action.payload }

        case SET_TOTAL_COUNT:
            return { ...state, totalCount: action.payload }

        default:
            return state
    }
}

export default usersReduser;
