const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';


export const followAC = (id) => ({ type: FOLLOW, payload: id, })
export const unfollowAC = (id) => ({ type: UNFOLLOW, payload: id, })
export const setUsersAC = (users) => ({ type: SET_USERS, payload: users, })


let initialState = {
    users: [
/*          {
            id: 1,
            avatarUrl: 'https://sun9-31.userapi.com/c849020/v849020113/195ec6/dm8KtRenxiQ.jpg',
            followed: true,
            fullName: 'Nikita',
            status: 'Каеф',
            location: { city: 'SPB', country: 'Russia' }
        },
        {
            id: 2,
            avatarUrl: 'https://i.pinimg.com/736x/ed/16/b9/ed16b9f0a63be6e6ab3a35da48031fb9.jpg',
            followed: false,
            fullName: 'Yura',
            status: 'Осенняя мразь',
            location: { city: 'NY', country: 'USA' }
        }  */
    ]
}

export const usersReduser = (state = initialState, action) => {

    switch (action.type) {
        case FOLLOW:
            let stateCopy = {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.payload) {
                        return { ...u, followed: true }
                    }
                    return u
                })
            }
            return stateCopy

        case UNFOLLOW: {
            let stateCopy = {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.payload) {
                        return { ...u, followed: false }
                    }
                    return u
                })
            }
            return stateCopy
        }
        case SET_USERS: {
            return {...state, users: [...state.users, ...action.payload]}
        }


        default:
            return state
    }
}

export default usersReduser;
