import React from 'react';
import { useState } from 'react';


const ProfileStatusHook = (props) => {
    let editMode = false;

    useState(false);

    return (
        <div>
            {
                    editMode
                    ? <input value={''}
                        onChange={''}
                        onBlur={''}
                        autoFocus={true}
                    />
                    : <span onDoubleClick={'activateEditMode'}>
                        {props.status || 'Write somesing'}
                    </span>
            }

        </div>
    )
}

export default ProfileStatusHook;
