import React from 'react';
import css from './ProfileInfo.module.css'
import Description from './Description/Description';
import ProfileAvatar from './ProfileAvatar/ProfileAvatar';
import { CircularProgress } from '@material-ui/core';
import topImg from '../../../assets/img/profileTop.jpg';


const ProfileInfo = (props) => {
  if(!props.profile) {
    return <CircularProgress></CircularProgress>
  }
    return (
      <div>
        <div className={css.profile__top}>

          <img alt="img" src={topImg}></img>
        </div>

        <div className={css.profile__info}>
          <ProfileAvatar img={props.profile.photos.large}/>
          <Description fullName={props.profile.fullName}/>
        </div>
    </div>
    )
}

export default ProfileInfo;
