import React from "react";
import css from './FormControls.module.css'

/* export const Textarea = ({input, meta, ...props}) => {
    const hasError = meta.error && meta.touched;
    return (
        <div className={css.formControl}>
            <textarea  {...input} {...props}></textarea>
            {
                hasError && <span className={css.alert}>{meta.error}</span>
            }

        </div>
    )
}
 */

/* export const Input = ({input, meta, ...props}) => {
    const hasError = meta.error && meta.touched;
    return (
        <div className={css.formControl + ' ' + (hasError ? css.error : '')}>
            <div className={css.wrap}>
            <input  {...input} {...props}></input>
            {
                hasError && <span className={css.alert}>{meta.error}</span>
            }
            </div>


        </div>
    )
} */

export const FormControl = ({input, meta, child, ...props}) => {
    const hasError = meta.error && meta.touched;
    return (
        <div className={css.formControl}>
            {props.child}
            {
                hasError && <span className={css.alert}>{meta.error}</span>
            }

        </div>
    )
}

export const Textarea = ({input, meta, child, ...props}) => {
  return <FormControl {...props}><textarea {...input} {...props}></textarea></FormControl>
}

export const Input = ({input, meta, child, ...props}) => {
    return <FormControl {...props}><input {...input} {...props}></input></FormControl>
  }
