import React from 'react';
import css from './NewPost.module.css'
import { addPostActionCreator, updateNewPostActionCreator } from '../../../../redux/profileReduser'

import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import DeleteIcon from '@material-ui/icons/Delete';
import CloudUploadIcon from '@material-ui/icons/CloudUpload';
import KeyboardVoiceIcon from '@material-ui/icons/KeyboardVoice';
import Icon from '@material-ui/core/Icon';
import SaveIcon from '@material-ui/icons/Save';

const useStyles = makeStyles((theme) => ({
  button: {
    margin: theme.spacing(1),
  },
}));


const NewPost = (props) => {
  const classes = useStyles();
let newPostEl = React.createRef();

let addPost = () => {
  props.dispatch(addPostActionCreator())
}

let onPostChange = () => {
  let text = newPostEl.current.value;  
  props.dispatch(updateNewPostActionCreator(text))
}

    return (
      <div className={css.posts__new}>
      <div>
        <textarea type="text" ref={newPostEl}  value={props.newPostText} onChange={onPostChange} />
        <button onClick={addPost} type="submit">Отправить</button>

        <Button
        variant="contained"
        color="primary"
        className={classes.button}
        endIcon={<Icon>send</Icon>}
      >
        Send
      </Button>

      </div>
    </div>
    )
}

export default NewPost;
